namespace SpriteKind {
    export const Platform = SpriteKind.create()
    export const Superplatform = SpriteKind.create()
    export const enemyProjectile = SpriteKind.create()
    export const bossShard = SpriteKind.create()
    export const killbrick = SpriteKind.create()
    export const boss = SpriteKind.create()
    export const flash = SpriteKind.create()
    export const finish = SpriteKind.create()
    export const homingEnemyProjectile = SpriteKind.create()
    export const powerup = SpriteKind.create()
    export const medpack = SpriteKind.create()
    export const ammoBox = SpriteKind.create()
    export const lifestealdrop = SpriteKind.create()
    export const sunaltar = SpriteKind.create()
    export const casing = SpriteKind.create()
}
sprites.onOverlap(SpriteKind.boss, SpriteKind.Projectile, function (sprite, otherSprite) {
    if (current_weapon == 0) {
        if (isSunAvatar == 0) {
            bossStatusBar.value += -1
        } else if (isSunAvatar == 1) {
            bossStatusBar.value += -20
        }
    } else if (current_weapon == 1) {
        if (isLifesteal == 0) {
            bossStatusBar.value += -3
        } else if (isLifesteal == 1) {
            bossStatusBar.value += -15
        }
    }
    sprites.destroy(otherSprite)
})
sprites.onOverlap(SpriteKind.Projectile, SpriteKind.Enemy, function (sprite5, otherSprite4) {
    sprites.destroy(sprite5, effects.fire, 500)
    sprites.destroy(otherSprite4, effects.fire, 500)
    info.changeScoreBy(1)
    shardDropChance = randint(1, 10)
    if (game.runtime() > 10000) {
        if (shardDropChance >= 1) {
            shard = sprites.create(img`
                . . . . . . 1 1 
                . . . 1 1 1 3 1 
                . 1 1 4 4 4 3 1 
                1 4 4 4 4 4 3 1 
                . 1 4 4 4 4 3 1 
                . 1 4 4 4 4 3 1 
                . . 1 1 1 4 3 1 
                . . . . . 1 1 . 
                `, SpriteKind.bossShard)
            shard.setPosition(otherSprite4.x, otherSprite4.y)
            shard.setVelocity(-25, 0)
            shard.setFlag(SpriteFlag.AutoDestroy, true)
        }
    }
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.killbrick, function (sprite, otherSprite) {
    if (current_weapon == 0) {
        if (isSunAvatar == 0) {
            sprites.destroy(otherSprite)
            sprites.destroy(sprite)
            game.gameOver(false)
        } else if (isSunAvatar == 1) {
            sprites.destroy(otherSprite, effects.starField, 1000)
            playerbar.value = 2
        }
    } else if (current_weapon == 1) {
        sprites.destroy(otherSprite, effects.starField, 1000)
        playerbar.value = 1
    }
})
sprites.onOverlap(SpriteKind.enemyProjectile, SpriteKind.Player, function (sprite, otherSprite) {
    if (current_weapon == 0) {
        sprites.destroy(sprite, effects.ashes, 500)
        playerbar.value += -3
    } else if (current_weapon == 1) {
        sprites.destroy(sprite, effects.starField, 500)
        playerbar.value += -2
    }
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.finish, function (sprite, otherSprite) {
    flashScreen()
    timer.after(1000, function () {
        game.setGameOverEffect(true, effects.melt)
        game.setGameOverMessage(true, "Eye down, eye defeated it!")
        game.gameOver(true)
    })
})
controller.B.onEvent(ControllerButtonEvent.Pressed, function () {
    if (current_weapon == 1) {
        current_weapon = 0
    } else if (current_weapon == 0) {
        current_weapon = 1
    }
})
function bossFight () {
    sprites.destroyAllSpritesOfKind(SpriteKind.bossShard, effects.spray, 500)
    bossStatusBar = statusbars.create(100, 5, StatusBarKind.EnemyHealth)
    _player.setPosition(10, 60)
    bossStatusBar.setLabel("EYELORD")
    bossStatusBar.setBarBorder(1, 4)
    bossStatusBar.setColor(2, 1)
    bossStatusBar.setStatusBarFlag(StatusBarFlag.SmoothTransition, true)
    bossStatusBar.positionDirection(CollisionDirection.Top)
    if (isSunAvatar == 0) {
        bossStatusBar.max = 150
        bossStatusBar.value = 150
    } else if (isSunAvatar == 1) {
        bossStatusBar.max = 300
        bossStatusBar.value = 300
    }
    eyeboss = sprites.create(img`
        ...fffffffffffff
        ...fffffffffffff
        ...fffffffffffff
        fff1111111111111
        fff1111111111111
        fff1111111111111
        fff2221111111111
        fff2221111111111
        fff2221111111111
        fff1112221111111
        fff1112221111111
        fff1112221111111
        fff5552221111111
        fff5552221111111
        fff5552221111111
        fff4442221111111
        fff4442221111111
        fff4442221111111
        fff5552221111111
        fff5552221111111
        fff5552221111111
        fff1112221111111
        fff1112221111111
        fff1112221111111
        fff2221111111111
        fff2221111111111
        fff2221111111111
        fff1111111111111
        fff1111111111111
        fff1111111111111
        ...fffffffffffff
        ...fffffffffffff
        ...fffffffffffff
        `, SpriteKind.boss)
    animation.runMovementAnimation(
    eyeboss,
    animation.animationPresets(animation.easeDown),
    2000,
    false
    )
    eyeboss.changeScale(2, ScaleAnchor.Right)
    animation.stopAnimation(animation.AnimationTypes.All, eyeboss)
    eyeboss.setPosition(150, 70)
    bossonscreen = 1
}
controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    if (cooldown == 0) {
        _player.vy = -40
        cooldown = 1
    }
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.Superplatform, function (sprite3, otherSprite3) {
    sprite3.setPosition(otherSprite3.x, otherSprite3.y - 45)
    sprites.destroy(otherSprite3)
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.Platform, function (sprite2, otherSprite2) {
    sprite2.setPosition(sprite2.x, otherSprite2.y - 15)
})
statusbars.onZero(StatusBarKind.EnemyHealth, function (status) {
    bossAlive = 0
    sprites.destroy(eyeboss, effects.disintegrate, 1000)
})
statusbars.onZero(StatusBarKind.Health, function (status) {
    game.setGameOverMessage(false, endgamemessages[randint(0, 4)])
    game.gameOver(false)
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.lifestealdrop, function (sprite, otherSprite) {
    music.play(music.melodyPlayable(music.jumpUp), music.PlaybackMode.InBackground)
    if (isLifesteal == 0) {
        playerbar.max = 15
        playerbar.value += -3
        timer.background(function () {
            isLifesteal = 1
            slashSpeed = 200
        })
    } else if (isLifesteal == 1) {
        playerbar.value += 5
    }
    sprites.destroy(otherSprite)
})
